import React, { useState } from "react"
import "./ProgressBar.css"

const ProgressBar = () => {
  const [mount, setMount] = useState(300)
  const wrapperWidth = 500
  let wrapperPercent = wrapperWidth / 100

  const add = () => {
    if (mount < wrapperWidth) setMount(mount + wrapperPercent * 10)
  }

  const subtract = () => {
    if (mount > 0) setMount(mount - wrapperPercent * 10)
  }

  const change = e => {
    let value = e.target.value
    if (value <= 100) 
        setMount(value * wrapperPercent)
    else 
        setMount(100 * wrapperPercent)
  }

  return (
    <>
      <div className="wrapper" style={{ width: `${wrapperWidth}px` }}>
        <div className="fill" style={{ width: `${mount}px` }} />
      </div>

      <button className="btn btn-primary btn-space" onClick={add}>
        Add 10%
      </button>
      <button className="btn btn-primary btn-space" onClick={subtract}>
        Subtract 10%
      </button>
      <input
        type="text"
        maxlength="3"
        className="input-space"
        style={{ display: "flex" }}
        onChange={change}
      />
    </>
  )
}

export default ProgressBar
