import { ProgressBar } from 'react-bootstrap'

import { useState,  } from 'react'

const TaskTen = () => {
  const [progres, setProgres] = useState(0)
  const getValue = e => {
    setProgres(e.target.value1)
  }

  return (
    <>
      <div style={{ display: 'flex', marginTop: '100px' }}>
        <ProgressBar
          style={{
            width: '250px',

            border: '1px solid black',
          }}
          now={progres}
        />
        <input
          style={{ width: '50px', height: '20px', marginLeft: '20px' }}
          onChange={getValue}
          value1={progres}
        />
      </div>
      <div>
        <button onClick={() => setProgres(progres + 10)}>Add 10%</button>
        <button onClick={() => setProgres(progres - 10)}>Minus 10%</button>
      </div>
    </>
  )
}

export default TaskTen
