import { useState } from 'react'

const TaskEight = props => {
  const { array } = props
  const [visible, setVisible] = useState(false)
  const [cardVisible, setCardVisible] = useState(false)
  const [indexVisible, setIndexVisible] = useState(2)

  return (
    <>
      <div>
        {array.map((item, i) => {
          return i < indexVisible ? (
            <div style={{ margin: '10px auto', width: '285px' }}>
              <div style={{ display: `${cardVisible ? 'block' : 'none'}` }}>
                {item}
              </div>
            </div>
          ) : (
            false
          )
        })}
      </div>
      <button
        style={{
          display: `${
            cardVisible && !(indexVisible >= array.length) ? 'block' : 'none'
          }`,
        }}
        onClick={() => setIndexVisible(indexVisible + 2)}>
        Показать ещё
      </button>
    </>
  )
}

export default TaskEight
