import { ProgressBar } from 'react-bootstrap'

import { useState,  } from 'react'

const TaskTen = () => {
  const [progres, setProgres] = useState(0)
  console.log(progres)
  const getValue = (e) => {
    if( e.target.vale >= 100){
      setProgres(100)
    } else if(e.target.value <= 0){
     setProgres(0) 
    }
    setProgres(e.target.value)
  }


  const add = ()=> {
    if( progres <= 90) {
    setProgres(parseInt(progres)+10)
    }
    console.log(progres)
  }
   const minus = ()=> {
    if( progres >= 10) {
    setProgres(parseInt(progres)- 10)
    }
    console.log(progres)
  }


  return (
    <>
    <div style= {{display:'flex',marginTop: '100px',}}>
      <ProgressBar
        style={{
          width: '250px',
          border: '1px solid black',
        }}
        now={progres}
      />
      <input style={{width:'50px',height:'20px', marginLeft:'20px'}} onChange ={getValue} value={progres} />
      </div>
      <div>

          <button  onClick ={minus}>Minus 10%</button>
          <button onClick ={add}>Add 10%</button>

      </div>
    </>
  )
}

export default TaskTen
