import { useState } from "react"




const TaskTen = () => {
const[progres, setProgres] = useState(50)

 const add = () =>{
   setProgres(progres+50)
 }

 const remove = () =>{
   setProgres(progres-50)
 }

  return (
    <>
    <div  style={{ width: "500px", height:'40px', border:'1px solid black', marginTop:'50px', }}>
        <div  style={{ width: `${progres}px`, height:'38px', backgroundColor:'green',overflow:'hidden' }} />
      </div>
      <div>
        <button onClick ={add}>add 10%</button>
        <button onClick ={remove}>remove 10%</button>
      </div>
    </>
  )
}

export default TaskTen
