import {usetate} from 'react'


const TaskTen = () => {

    function AddProgress (){
        const elem = document.createElement('div')
        elem.innerHTML = "Water"
       const main = document.getElementById('main')
       main.appendChild(elem)
       console.log(5);

    }
    function RemoveProgress (elem){

        elem.innerHTML = "Water"
       const main = document.getElementById('main')
       main.removeChild(elem)
       console.log(5);

    }

    return (
        <>
        <div id='main' style = {{width:'300px', height:'20px', border:'1px solid black', display:'flex'}}></div>
        <button onClick ={() => AddProgress()}>PLus 10</button>
        <button onClick ={() => RemoveProgress()}>Minus 10</button>
        </>
    )
}

export default TaskTen
