import React, { useState } from "react"


const TaskTvelve = (props) => {
    const{countArticles, limit} = props

  let [value, setValue] = useState(false)

  const toggle = i => {
    if (value !== i) {
      return setValue(i)
    }
  }

  const renderElements = [...Array(5)].map((item, i) => {
    i += 1
    return (
      <span>
        <a
          href={`#${value}`}
          onClick={() => toggle(i)}
          style={{ color: value == i ? "#000" : "#d33" }}>
          {i}
        </a>
      </span>
    )
  })

  return (
    <div className="Task12">
      <div
        onClick={() => setValue((value -= 1))}
        style={ {display:`${value == 1 ? "none" : "block "}` }}>
        Prev
      </div>

      {renderElements}

      <div
        onClick={() => setValue((value += 1))}

        style={ {display:`${value == renderElements.length ? "none" : "block"}` }}>
        Next
      </div>
    </div>
  )
}

export default TaskTvelve
